    private void initView() {
        preferences = PreferenceManager.getDefaultSharedPreferences(this);

        String url = Url.baseurl + "/nevent_data";
        JSONObject json = new JSONObject();
        JsonObjectRequest jsonObjectRequest = new JsonObjectRequest(Request.Method.POST, url, json, new Response.Listener<JSONObject>() {
            @Override
            public void onResponse(JSONObject response) {
                Log.d("ashok_news", response.toString());
                try {
                    Gson gson = new Gson();
                    NewsUpdateResponseModel nm = gson.fromJson(response.toString(), NewsUpdateResponseModel.class);
                    if (nm.getSuccess()) {

                        NewsAdapter communityChatAdapter = new NewsAdapter(getBaseContext(), nm.getData(), "/nevent_like_unlike_post", "/nevent_comments", "/nevent_comments_post", false, avi);
                        recyclerView.setAdapter(communityChatAdapter);
                    } else {
                       // // Toast.makeText(getBaseContext(), "" + response.getString("message"), // Toast.LENGTH_SHORT).show();
                        //login page
                        SharedPreferences preferences = PreferenceManager.getDefaultSharedPreferences(getBaseContext());
                        SharedPreferences.Editor editor = preferences.edit();
                        editor.clear();
                        editor.apply();
                        startActivity(new Intent(getBaseContext(), LoginActivity.class));
                    }
                } catch (Exception e) {
                    e.printStackTrace();
                }
                stopAnim();

            }
        }, new Response.ErrorListener() {
            @Override
            public void onErrorResponse(VolleyError error) {

                stopAnim();
                Utility.INSTANCE.customSnackBar(recyclerView,NewsUpdate.this,error.toString(),
                        ContextCompat.getColor(NewsUpdate.this,R.color.error),R.drawable.ic_error);            }
        }) {
            @Override
            public Map<String, String> getHeaders() throws AuthFailureError {
                Map<String, String> header = new HashMap<>();
                header.put("Content-Type", "application/json");
                header.put("token", preferences.getString(Datas.token, ""));
                header.put("lid", preferences.getString(Datas.lagnuage_id, "1"));

                return header;
            }
        };
        MySingleton.getInstance(this).addToRequestQueue(jsonObjectRequest);
    }


    public void back(View view) {
        finish();
    }
